import './App.css';
import pro_pic from './img/profile_pic2.jpg';
import React, { Component } from "react";
import ReactRoundedImage from "react-rounded-image";

function AboutMe() {
  return (
    <div className="wrapper">
        <img src={pro_pic} alt="profile pic" className="profile_pic" />
        <p className="about_me">
        I am a first-year PhD student studying computer science at Carnegie Mellon University. <br />

        In Spring 2021, I completed my undergraduate at the University of Kansas. While there, I 
        double-majored in computer science and mathematics, and minored in visual arts (emphasis 
        in painting). At KU, I also worked as a research assistant under the guidance of Dr. 
        Suzanne Shontz in scientific computing.

        I am broadly interested in solving cool problems at the interface of mathematics and 
        computer science. My research interests particularly include:
        <ul>
          <li>type theory</li>,
          <li>language design</li>
        </ul>
        </p>

        <div>
        {/* <ReactRoundedImage 
          className="profile_pic"
          image={pro_pic}
          roundedColor="#321124"
          imageWidth="300"
          imageHeight="300"
        />
        <p>mdotzel [at] andrew [dot] cmu [dot] edu</p> */}
      </div>
        
      
    </div>
  );
}

export default AboutMe;
